import base64
import os
import random
import time
from io import BytesIO
from datetime import datetime, timedelta

import requests
from PIL import Image, ImageDraw, ImageEnhance, ImageFilter, ImageFont

from config import Config
from Music.core.logger import LOGS
from Music.helpers.formatters import formatter
from Music.helpers.youtube import Hell_YTS


class Thumbnail:
    def __init__(self) -> None:
        self.base = "https://www.youtube.com/watch?v="
        # New modern music bot thumbnail base64 string
        self.xcb_svg = """

        """

    def generate(self, overlay_size, position, vidid) -> str:
        if vidid == "telegram":
            return Config.TELEGRAM_IMG
        try:
            xcb_svg = self.xcb_svg.replace(" ", "").replace("\n", "")
            xcb_tmx = base64.b64decode(xcb_svg)

            font_color = (255, 255, 255)
            font_path = "./resources/fonts/fmt.otf"
            font_ext_path = "./resources/fonts/xcb.ttf"

            font = ImageFont.truetype(font_path, 50)
            font_ext = ImageFont.truetype(font_ext_path, 80)

            url = self.base + vidid
            yt = Hell_YTS(url, 1).to_dict()[0]
            video_id = yt["id"]
            title = yt["title"]
            view_count = int(yt["views"].split(" ")[0].replace(",", ""))
            published = yt["publish_time"]
            duration = yt["duration"]
            thumbnail_url = f"https://img.youtube.com/vi/{video_id}/sddefault.jpg"

            response = requests.get(thumbnail_url)
            thumbnail_path = f"cache/{video_id}_{round(time.time())}.jpg"
            with open(thumbnail_path, "wb") as f:
                f.write(response.content)

            image = Image.open(thumbnail_path)
            width, height = image.size
            size = min(width, height)
            left = (width - size) // 2
            top = (height - size) // 2
            right = left + size
            bottom = top + size
            image = image.crop((left, top, right, bottom))

            resized_thumbnail_path = f"{thumbnail_path[:-4]}_re.jpg"
            image.save(resized_thumbnail_path)
            os.remove(thumbnail_path)

            base_image = Image.open(BytesIO(xcb_tmx))
            draw = ImageDraw.Draw(base_image)

            # Get current India time (UTC+5:30)
            utc_time = datetime.utcnow()
            india_time = utc_time + timedelta(hours=5, minutes=30)
            time_string = india_time.strftime("%I:%M %p")  # Format: 07:30 PM

            text_length = draw.textlength(Config.BOT_NAME, font=font_ext)
            img_width, _ = base_image.size
            cent_x = (img_width - text_length) // 2

            # Draw bot name at bottom - SINGLE TEXT ONLY
            draw.text(
                (cent_x, 960),
                Config.BOT_NAME,
                font=font_ext,
                fill=(0, 255, 234),
            )

            # Apply color shift effect
            base_image_hsv = base_image.convert("HSV")
            h, s, v = base_image_hsv.split()

            shifting_seed = lambda: round(random.Random().uniform(0.0, 1.0), 1)
            hue_shift = shifting_seed()
            hue_modified = h.point(lambda i: (i + int(255 * hue_shift)) % 256)
            modified_image_hsv = Image.merge("HSV", (hue_modified, s, v))

            base_image = modified_image_hsv.convert("RGB")
            overlay_image = Image.open(resized_thumbnail_path)

            # Resize overlay to fit the larger frame (620x620 to fit in 650x650 frame with padding)
            overlay_size = 620
            overlay_image = overlay_image.resize((overlay_size, overlay_size))

            # Add border effect to video thumbnail
            border_size = 12
            border_color = (138, 43, 226, 220)  # Purple color
            border_image = Image.new(
                "RGBA", (overlay_size, overlay_size), (255, 255, 255, 0)
            )
            mask_draw = ImageDraw.Draw(border_image)
            mask_draw.rounded_rectangle(
                [(0, 0), (overlay_size - 1, overlay_size - 1)],
                radius=15,
                outline=border_color,
                width=border_size,
            )

            blurred_border_image = border_image.filter(
                ImageFilter.GaussianBlur(radius=12)
            )
            border_brightness = ImageEnhance.Brightness(blurred_border_image)
            border_image = border_brightness.enhance(1.6)
            result_image = Image.alpha_composite(
                overlay_image.convert("RGBA"), blurred_border_image
            )
            result_image = result_image.filter(ImageFilter.SMOOTH)

            # Format title
            if len(title) > 38:
                title = title[:35] + "..."

            # Humanize view count
            humanize = (
                lambda view_count: str(view_count)
                if view_count < 1000
                else "{:.1f}k".format(view_count / 1000)
                if view_count < 1_000_000
                else "{:.1f}M".format(view_count / 1_000_000)
                if view_count < 1_000_000_000
                else "{:.1f}B".format(view_count / 1_000_000_000)
            )

            view_count = humanize(view_count)

            # Draw video information - TEXT ABOVE THE LINES (NOT BELOW)
            draw = ImageDraw.Draw(base_image)
            text_x = 840
            info_y_start = 380
            line_spacing = 110
            
            # Title - TEXT POSITIONED ABOVE THE LINE
            # Line is at info_y_start + 25, so text should be at info_y_start - 15 to appear above
            title_y = info_y_start - 15
            draw.text((text_x + 180, title_y), title, font=font, fill=font_color)
            
            # Duration - TEXT POSITIONED ABOVE THE LINE
            duration_y = (info_y_start + line_spacing) - 15
            draw.text((text_x + 250, duration_y), duration, font=font, fill=font_color)
            
            # Views - TEXT POSITIONED ABOVE THE LINE
            views_y = (info_y_start + 2 * line_spacing) - 15
            draw.text((text_x + 180, views_y), view_count, font=font, fill=font_color)
            
            # Playing Since - TEXT POSITIONED ABOVE THE LINE
            time_y = (info_y_start + 3 * line_spacing) - 15
            time_text = f"{time_string} (India Time)"
            draw.text((text_x + 420, time_y), time_text, font=font, fill=font_color)

            # Paste video thumbnail on base image (adjusted position for larger frame)
            paste_position = (95, 165)  # Centered in the frame
            base_image.paste(result_image, paste_position, result_image)
            
            rand_key = formatter.gen_key(f"thumb_{video_id}")
            result_path = f"{rand_key}.png"
            base_image.save(result_path)

            os.remove(resized_thumbnail_path)
            return result_path
        except Exception as e:
            LOGS.error(e)
            return None


thumb = Thumbnail()
